/*Table structure for table `layanan` */

DROP TABLE IF EXISTS `layanan`;

CREATE TABLE `layanan` (
  `id_layanan` int(5) NOT NULL,
  `id_kategori` int(5) NOT NULL,
  `username` varchar(30) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL,
  `judul` varchar(100) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL,
  `judul_seo` varchar(100) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL,
  `headline` enum('Y','N') CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL DEFAULT 'Y',
  `isi_layanan` text CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL,
  `hari` varchar(20) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL,
  `tanggal` date NOT NULL,
  `jam` time NOT NULL,
  `gambar` varchar(100) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL,
  `dibaca` int(5) NOT NULL DEFAULT '1',
  `tag` varchar(100) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci;

/*Data for the table `layanan` */

insert  into `layanan`(`id_layanan`,`id_kategori`,`username`,`judul`,`judul_seo`,`headline`,`isi_layanan`,`hari`,`tanggal`,`jam`,`gambar`,`dibaca`,`tag`) values (1,4,'admin','JASA CARGO JALUR DARAT','jasa-cargo-jalur-darat','Y','<p>Jasa pengiriman barang murah melalui jalur darat menggunakan armada truk dan kereta api untuk kebutuhan perusahaan maupun retail tujuan Sumatera, Jawa, Bali dan Nusa Tenggara Barat.</p>\r\n','Senin','2022-11-21','16:33:28','background.png',0,'film'),(2,5,'admin','JASA CARGO JALUR LAUT','jasa-cargo-jalur-laut','Y','<p>Jasa pengiriman barang murah melalui jalur laut ke seluruh wilayah nusantara melalui pelabuhan menggunakan berbagai jenis kapal. Baik Pelni ataupun Roro</p>\r\n','Jumat','2022-11-11','15:13:16','ocean1.jpg',0,'mobil'),(3,6,'admin','JASA MULTIPLE SERVICES','jasa-multiple-services','Y','<p>Prioritas kami meliputi multiple layanan service yang handal yaitu :</p>\r\n\r\n<p>Dengan FESA Antaran Logsitik pengiriman motor atau mobil menjadi murah dengan berbagai pilihan moda jalur transportasi dengan estimasi tarif terjangkau, cepat dan aman.</p>\r\n\r\n<p>Biaya pengiriman alat berat menjadi murah, baik untuk industri maupun konstruksi juga dapat Anda kirim ke seluruh wilayah Indonesia tanpa Anda harus ragu.</p>\r\n\r\n<p>Jasa pengiriman barang menggunakan Container, FCL atau LCL untuk tujuan Sumatera, Jawa, Bali, NTB, NTT, Kalimantan, Sulawesi, Maluku &amp; Papua.</p>\r\n','Rabu','2022-11-16','20:14:14','pegawai.png',0,'komputer'),(0,32,'admin','JASA CARGO JALUR UDARA','jasa-cargo-jalur-udara','Y','<p>Jasa pengiriman barang cargo murah via jalur udara ke wilayah Sumatera, Jawa, Bali, NTB, NTT, Kalimantan, Sulawesi hingga Papua, dengan pelayanan kilat.</p>\r\n','Jumat','2022-11-11','15:14:26','air1.jpg',0,'laskar-pelangi');

