insert  into `sekilasinfo`(`id_sekilas`,`info`,`isi`,`tgl_posting`,`gambar`,`aktif`,`urutan`) values (1,'<p>We Grow with The Customer Trust, Through Quality, Cost and Delivery Time!</p>','<p>PT Fesa Antaran Logistik didirikan di Jakarta tanggal 22 Januari 2017 sesuai dengan Akte Pendirian Perusahaan yang diterbitkan oleh Notaris Sri Juwaryati, SH. MKN dengan nomor 57. Perusahaan Kami bergerak dibidang Pelayanan Jasa Pengankutan dan Distribusi Pengiriman Barang ke seluruh wilayah Indonesia melalui Darat dengan fasilitas Trucking(Line haul) maupun Kereta Api, Cargo Laut melalui Kapal Cepat maupun Tol Laut dan Cargo Udara.</p>\r\n','2010-04-11','fesa.png','Y',1),(2,'<div class=\"subtitle\">\r\n                        <p style=\"font-size:17px;margin-bottom:6px;color:#ffffff;\">\r\n                            Your Package, Your Rules</p>\r\n                    </div>\r\n                    <div class=\"title-heading\">\r\n                        <h2 class=\"heading-tag\" style=\"color:#ffffff;font-size:43px;line-height:58px;font-weight:700; \">\r\n                            Digital Freight That<br /> Saves Your Time!</h2>\r\n                    </div>','','2010-04-11','fesa_layout.jpg','Y',2),(15,'<div class=\"subtitle\">\r\n                        <p style=\"font-size:14px;margin-bottom:12px;color:#ff5e14;\">\r\n                            Services We Offer</p>\r\n                    </div>\r\n                    <div class=\"title-heading\">\r\n                        <h2 class=\"heading-tag\" style=\"line-height:46px;font-weight:700; \"> We\r\n                            Manage Lead Logistics<br /> For World’s\r\n                            Multinational<br /> Companies.</h2>\r\n                    </div>','<p>Our global logistics expertise, advanced supply chain technology &amp; customized logistics solutions will help you analyze, develop and implement successful supply chain management strategies from end-to-end.</p>\r\n','2022-11-09','shutterstock_647680018.jpg','Y',3);

